import React from 'react';
import Modal from 'react-modal';
import { useState } from 'react';
import Button from './Modal1';
import styled from 'styled-components';
import style from './Modal1.module.css';

// export const Modal1 = ({open, setOpen}) => {
export const Modal1 = (props) => {

    const { open, setOpen, initList, setInitList } = props;
    // const {initList, setInitList} = props;

    const close = () => {
        setOpen(false);
    }
  
    const [input, setInput] = useState('');
    const [input2, setInput2] = useState('');

    const handleInputchange = (e) => {
        setInput(e.target.value)
    }

    const handleInputchange2 =(e) => {
        setInput2(e.target.value)
        }

    const handleUpload = () => {
        // "asdfsadfsdaf"
        // { key: value }
        const item = {
            pk: initList.length - 1,
            title: input,
            contents : input2,
            reply: []
        }
       setInitList([...initList, item])
        close()
        setInput("")
        setInput2("")
    }

    const Button =styled.div`
    padding: 30px
    width: 200px
    background: green;`


    console.log(input);
    return (
    <Modal isOpen={open}>
        <div className= {style.button1}>
        <h2>글쓰기</h2>
        
        <p>제목</p>
        <input type="text" value={input} onChange = {handleInputchange}></input> 
        <p>내용</p>
        <input type="text" value={input2} onChange = {handleInputchange2}></input> 
        < button onClick= {handleUpload}>Upload</button>
        <p></p>
        <button onClick={close}>창닫기 X</button>
        </div>
        
    </Modal>
  )
}

