import React from 'react'

export const Comments = (props) => {

    const { item } = props;
    <input type="checkbox"></input>

    if(item.highlight){
        return <p><span style={{color: "#00ff00"}}>{item.comment}</span></p>
        
    }
    return <p><span style={{color: "#000"}}>{item.comment}</span></p>
}

export default Comments
