import React from 'react'
import { useLocation } from "react-router-dom";

export const Detail = () => {
    const location = useLocation();

    const item = location.state.data;

    const [initItem, setInitItem] = React.useState(item);

  return (
    <>
        <p>{initItem.title}</p>
        <p>{initItem.contents}</p>
        <hr />
        {initItem.reply.map((reply, index) => {
            return (
                <>
                <p>{reply.comment}</p>
                </>
            )
        })}
        <input />
    </>
  )
}


export default Detail
