import React from 'react'
import { useLocation } from "react-router-dom";

export const Detail = () => {
    const location = useLocation();

    const item = location.state.data;

    console.log(item)


  return (
    <>
        {item.title}
        {item.contents}
        {item.reply.map((reply, index) => {
            return (
                <>
                {reply.comment}
                </>
            )
        })}
    </>
  )
}


export default Detail
