import React from 'react'
import { useLocation } from "react-router-dom";
import { Comments } from '../components/Comments';

export const Detail = () => {
    const location = useLocation();

    const item = location.state.data;

    const [initComment, setInitComment] = React.useState(item.reply);
    const [comment, setComment] = React.useState("");
    // const number = Math.floor(Math.random() * initComment.length);
    //리스트의 컨텐츠의 갯수
    //const replynumber = list.reply.map((replynumbers) => {replynumbers});
    //replynumber.length  

    const save = (temp) => {
        const a = {
            comment: temp
         }

        setInitComment([...initComment, a])
        setComment("")
    }


    const choose = () => {
        const number = Math.floor(Math.random() * initComment.length);
        console.log(number)
        alert(`당첨자 댓글은${initComment[number].comment}`)
        setInitComment(
            initComment.map((data, index) => {
                index === number ? { ...data, highlight: true } : comment
            })       
        )
    }
  return (
    <>
        <p>{item.title}</p>
        <p>{item.contents}</p>
        <hr />
        <button onClick={choose}>추첨하기</button> 
        **//버튼 누르면 number 함수

        {initComment.map((reply, index) => {
            return (
                <Comments item={reply} />
            )
        })}
        <input value={comment} onChange={(e)=>{setComment(e.target.value)}} />
        <button onClick={() => {save(comment)}}>확인</button>
    </>
  )
}
// 변수를 설정을 하고 변수가 뭔지 적어줘야함

export default Detail
