import React from 'react';
import Write from "./Write"
import { Modal1 } from "./Modal1"
import { useNavigate } from "react-router-dom";
import styled from 'styled-components';
import style from './post.module.css';
import hs from './img/hs1.png';

const Post = () => {

  const navigate = useNavigate();


  const list = [
    {
      pk: 0,
      title: "제목",
      contents: "내용",
      reply: [
        {
           comment: '댓글',
           highlight: false
        },
        {
          comment: '댓글1',
          highlight: false
       },
       {
        comment: '댓글2',
        highlight: false
     },
     {
      comment: '댓글3',
      highlight: false
     }
      ]
    },
    {
      pk: 0,
      title: "제목",
      contents: "내용",
      reply: [
        {
           comment: '댓글',
           highlight: false
        },
        {
          comment: '댓글1',
          highlight: false
       },
       {
        comment: '댓글2',
        highlight: false
     },
     {
      comment: '댓글3',
      highlight: false
     }
      ]
    }
  ]

  // map 리스트 나열할 때 (item 변수 이름,순서)
  // const a = () => {
  //   const list = ['a', 'b', 'c', 'd'];

  //   // for(let i=0; i < list.length; i ++){
  //   //   console.log(list[i])
  //   // }

  //   for(const item in list){
  //     console.log(item)
  //   }
  // }


  const [open, setOpen] = React.useState(false);
  const [initList, setInitList] = React.useState(list);

  const write = () => {
    navigate('/write')
  }
  const modal = () => {
    setOpen(true)
  }

  React.useEffect(() => {
    console.log(open);
  }, [open])

  const add = () => {
    const listItem =  {
      pk: 0,
      title: "제목",
      contents: "내용",
      reply: [
        {
          comment: '댓글1',
          highlight: false
       },
       {
        comment: '댓글2',
        highlight: false
     },
     {
      comment: '댓글3',
      highlight: false
     }
      ]
    }
    setInitList([...initList, listItem])
    // setInitList(initList.push(listItem))
  }

  const detail = (item) => {
    navigate('/detail', {
      state: {
        data: item
      }
    }
    )
  }

  const remove = (index) => {
    // setInitList(initList.filter((it) => {it.pk !== index }))

    setInitList(initList.filter(it => it.pk !== index))
  }




  return (
    <>
      {/* <Modal1 open={open} /> */}
      <Modal1 open={open} setOpen={setOpen} initList={initList} setInitList={setInitList}/>
      <header>
        <img className={style.hs} src={hs}></img>
        <h1 className={style.titleName} img src={hs} >홍시 팬 클럽</h1> 
        <hr />
        < button className= {style.button1} onClick={() => write()}> 다른페이지 이동 </button>
        <button className= {style.button1} onClick={() => {modal(); /*handleUpload();*/}}> 글 작성 </button>
        <button className= {style.button1} onClick={add}> 추가 테스트 </button>
      </header>

      {
        initList.map((a, b) => {
          return (
            <>
              <hr />
              <div style={{display: 'flex'}} onClick={() => {detail(a)}}>
              <h1>{b}</h1> 
              <h1>{a.title}</h1>
              <button onClick={() => {remove(a.pk)}}>삭제</button>
              </div>
              {/* <h3> {a.reply[0].comment} </h3>  */}
              <hr />
            </>
          )
        })
      }
      
    </>
  );
}

export default Post;
