import React from 'react';
import Write from "./Write"
import { Modal1 } from "./Modal1"
import { useNavigate } from "react-router-dom";




const a = () => {

}
const Post = () => {

  const navigate = useNavigate();


  const list = [
    {
      pk: 0,
      title: "제목",
      contents: "내용",
      reply: [
        {
           comment: '댓글',
           highlight: false
        },
        {
          comment: '댓글1',
          highlight: false
       },
       {
        comment: '댓글2',
        highlight: false
     },
     {
      comment: '댓글3',
      highlight: false
     }
      ]
    },
    {
      pk: 0,
      title: "제목",
      contents: "내용",
      reply: [
        {
           comment: '댓글',
           highlight: false
        },
        {
          comment: '댓글1',
          highlight: false
       },
       {
        comment: '댓글2',
        highlight: false
     },
     {
      comment: '댓글3',
      highlight: false
     }
      ]
    },
  ]

  // map 리스트 나열할 때 (item 변수 이름,순서)
  // const a = () => {
  //   const list = ['a', 'b', 'c', 'd'];

  //   // for(let i=0; i < list.length; i ++){
  //   //   console.log(list[i])
  //   // }

  //   for(const item in list){
  //     console.log(item)
  //   }
  // }


  const [open, setOpen] = React.useState(false);
  const [initList, setInitList] = React.useState(list);

  const write = () => {
    navigate('/write')
  }
  const modal = () => {
    setOpen(true)
  }

  React.useEffect(() => {
    console.log(open);
  }, [open])

  const add = () => {
    const listItem = { title: '제목', contents: '내용1' }
    setInitList([...initList, listItem])
    // setInitList(initList.push(listItem))
  }

  const detail = (item) => {
    navigate('/detail', {
      state: {
        data: item
      }
    }
    )
  }

  const remove = (index) => {
    // setInitList(initList.filter((it) => {it.pk !== index }))

    setInitList(initList.filter(it => it.pk !== index))
  }

  return (
    <>
      {/* <Modal1 open={open} /> */}
      <Modal1 open={open} setOpen={setOpen} initList={initList} setInitList={setInitList}/>
      <header>
        <h1 className>게시판</h1>
        <hr />
        <button onClick={() => write()}> 다른페이지 이동 </button>
        <button onClick={() => {modal(); /*handleUpload();*/}}> 글 작성 </button>
        <button onClick={add}> 추가 테스트 </button>
        <button> 글 삭제</button>
        <button> 글 수정</button>
      </header>

      {
        initList.map((a, b) => {
          return (
            <>
              <hr />
              <div style={{display: 'flex'}} onClick={() => {detail(a)}}>
              <h1>{a.title}</h1>
              <h3>{b}</h3>
              <button onClick={() => {remove(a.pk)}}>삭제</button>
              </div>
              {/* <h3> {a.reply[0].comment} </h3>  */}
              <hr />
            </>
          )
        })
      }
      {/* {
        initList.map((c,d) => {
          return (
            <h1> {d} </h1>
            
          )
        }) 
      }
 */}

    </>
  );
}

export default Post;
