import React from 'react'
import { useLocation } from "react-router-dom";

export const Detail = () => {
    const location = useLocation();

    const item = location.state.data;

    const [initItem, setInitItem] = React.useState(item);
    const [initComment, setInitComment] = React.useState(item.reply);
    const [comment, setComment] = React.useState("");


    const save = (temp) => {
        const a = {
            comment: temp
         }

        setInitItem([...initComment, a])
        setComment("")
    }

  return (
    <>
        <p>{initItem.title}</p>
        <p>{initItem.contents}</p>
        <hr />
        {initComment.map((reply, index) => {
            return (
                <>
                <p>{reply.comment}</p>
                </>
            )
        })}
        <input value={comment} onChange={(e)=>{setComment(e.target.value)}} />
        <button onClick={() => {save(comment)}}>확인</button>
    </>
  )
}


export default Detail
