  import React from 'react';
  import Write from "./Write"
  import {Modal1} from "./Modal1"
  import { useNavigate } from "react-router-dom";

  const Post = () => {

    const navigate = useNavigate();
    const [open, setOpen] = React.useState(false);

    const list = [
      {
        title: "제목",
        contents: "내용",
        reply: [
          {comment: '댓글'} 
        ]
      },
      {
        title: "제목a",
        contents: "내용a",
        reply: [
          {comment: '댓글a'} 
        ]
      }
    ]

    // const a = () => {
    //   const list = ['a', 'b', 'c', 'd'];

    //   // for(let i=0; i < list.length; i ++){
    //   //   console.log(list[i])
    //   // }

    //   for(const item in list){
    //     console.log(item)
    //   }
    // }

    const [initList, setInitList] = React.useState(list);

    const write = () => {
      navigate('/write')
    }
    const modal = () => {
      setOpen(true)
    }

    React.useEffect(() => {
      console.log(open);
    },[open])

    const add = () => {
      const list = {title: '제목1', contents:'내용1'}
      setInitList([...initList, list])
    }

    return (
      <>
      {/* <Modal1 open={open} /> */}
      <Modal1 open={open} setOpen={setOpen} />
      <header>
      <h1>게시판</h1>
      <hr/>
      <button onClick={write}> 글 작성 </button>
      <button onClick={modal}> modal </button>
      <button onClick={add}> 추가 테스트 </button>
      </header>

      {
        initList.map((item, index) => {
        return (
          <>
          <hr />
          <h3> {item.title} </h3> 
          {/* <h5> {item.contents} </h5>  */}
          {/* <h5 placeholder={item.contents} readOnly/> */}
          <hr />
          </>
        ) 
      })
      }
      </>
    );
  }

  export default Post;
